set-document.ts.content:
import { documents } from '@nitric/sdk';

interface Product {
    id: string;
    name: string;
    description: string;
}

const collection = documents().collection<Product>('products');

await collection.doc('nitric').set({
    id: 'nitric',
    name: 'nitric',
    description: 'A development framework!',
});
